import React from "react";
import './board.css'

class Board extends React.Component{
    constructor(props){
        super(props)
        this.state={
            player:1
        }
    }
    render(){ 
        return (
            <div>
                <h2>Welcome to the Board</h2>
                <div id="outline">
                    <table>
                        <tr><td onclick="this.style.backgroundColor = 'Red';"></td><td id="2"></td><td id="3"></td><td id="4"></td><td id="5"></td><td id="6"></td></tr>
                        <tr><td id="7"></td><td id="8"></td><td id="9"></td><td id="10"></td><td id="11"></td><td id="12"></td></tr>
                        <tr><td id="13"></td><td id="14"></td><td id="15"></td><td id="16"></td><td id="17"></td><td id="18"></td></tr>
                        <tr><td id="19"></td><td id="20"></td><td id="21"></td><td id="22"></td><td id="23"></td><td id="24"></td></tr>
                        <tr><td id="25"></td><td id="26"></td><td id="27"></td><td id="28"></td><td id="29"></td><td id="30"></td></tr>
                        <tr><td id="31"></td><td id="32"></td><td id="33"></td><td id="34"></td><td id="35"></td><td id="36"></td></tr>
                        <tr><td id="37"></td><td id="38"></td><td id="39"></td><td id="40"></td><td id="41"></td><td id="42"></td></tr>
                    </table>
                </div>
            </div>
        );
    }
    CALL = (i) => {
        
        let col=i%6
        let row=Math.floor(i/6)
        // console.log((row*6)+col, i)
        const grid= document.getElementById("outline");
        const cells = grid.getElementsByTagName("td");
        // const cells = grid.getElementsByName
        if(this.right(row)==true){
            console.log("right function")
            return "winner"
        }
        let right=[row+1, row+2, row+3]
        let left=[row-1, row-2, row-3]
        let top=[col+1, col+2, col+3]
        let down=[col-1, col-2, col-3]
        let topdiagonal=[]
        let topdiagonal2=[]

    }

    right = (i) => {
        return true
        console.log("entered")
    }
        
    componentDidMount() {
        const grid= document.getElementById("outline");
        const cells = grid.getElementsByTagName("td");
        for(let i=0; i < cells.length; i=i+1){
            cells[i].addEventListener("click", ()=>{
                if(this.state.player==1 && cells[i].style.backgroundColor!="green"){
                    var row=i%6
                    var col=Math.floor(i/6)
                    // console.log(row, col)
                    // for(let j=i; j<cells.length; j=j+6){ 
                        
                    //     if(cells[j].style.backgroundColor!="red"){
                    //         cells[j-6].style.backgroundColor="none"
                    //         cells[j].style.backgroundColor="red";
                    //     }
                    // }
                    // console.log(i)
                    cells[i].style.backgroundColor="red";
                    // console.log(i)
                    this.CALL(i)
                    this.setState({player:2});
                }
                else if(this.state.player==2 && cells[i].style.backgroundColor!="red"){
                    cells[i].style.backgroundColor="green";
                    // cells[(i-1)].style.backgroundColor="None"
                    // console.log(i)
                    this.CALL(i)
                    var row=Math.floor(i/6)
                    var col=i%6
                    // console.log(row, col)
                    this.setState({player:1});
                }
            });
        }
    }
}
export default Board;
