        }
    }
    render(){ 
        return (
            <div>
                <h2>Welcome to the Board</h2>
                <div id="outline">
                    <table>
                        <tr><td onclick="this.style.backgroundColor = 'Red';"></td><td id="2"></td><td id="3"></td><td id="4"></td><td id="5"></td><td id="6"></td></tr>
