                        <tr><td id="31"></td><td id="32"></td><td id="33"></td><td id="34"></td><td id="35"></td><td id="36"></td></tr>
                        <tr><td id="37"></td><td id="38"></td><td id="39"></td><td id="40"></td><td id="41"></td><td id="42"></td></tr>
                    </table>
                </div>
            </div>
        );
    }
    CALL = (i) => {
        
        // let col=i%6
        // let row=Math.floor(i/6)
        // console.log((row*6)+col, i)
        const grid= document.getElementById("outline");
        const cells = grid.getElementsByTagName("td");
        const clr=cells[i].style.backgroundColor
        // const cells = grid.getElementsByName
        const l=cells.length
        console.log(clr)
        if(this.right(i, clr,l)==true){
            
            // console.log("player is winner")
        }
        else if(this.left(i, clr, l)==true){
            // console.log("Winner")
        }
        else if(this.top(i, clr, l)==true){
            // console.log("winner")
        }
        else if(this.down(i, clr, l)==true){
            // console.log("winner")
        }
        else if(this.topdiagonal(i, clr, l)==true){
            // console.log("winner")
        }
        else if(this.downdiagonal(i, clr, l)==true){
            // console.log("winner")
        }
        else{
            return;
        }
    }

    right = (i, c, l) => {
        const k=i+3
        const grid= document.getElementById("outline");
        const cells = grid.getElementsByTagName("td");
        var cnt=0;
        if(k<=l){
            for (let j=i; j<=k; j++){
               if(cells[j].style.backgroundColor==c) {
                   cnt=cnt+1;
                   if(cnt==4){
                       console.log("right winner")
                       return true;
                   }
               }
            }
        }
        
            
        
    }
    left = (i, c, l) => {
        console.log("left")
        const k=i-3
        const grid= document.getElementById("outline");
        const cells = grid.getElementsByTagName("td");
        var cnt=0;
        if(k>=0){
            for (let j=i; j>=k; j--){
               if(cells[j].style.backgroundColor==c) {
                   cnt=cnt+1;
                   if(cnt==4){
                        console.log("left winner")
                       return true;
                   }
               }
            }
        }
        
    }
    top = (i, c, l) => {
        console.log("top")
        
    }
    down = (i, c, l) => {
        // console.log("down")
        
    }
    topdiagonal = (i, c, l) => {
        // console.log("topdiagonal")
        
    }
    downdiagonal = (i, c, l) => {
        // console.log("downdiagonal")
        
    }
        
    componentDidMount() {
        const grid= document.getElementById("outline");
        const cells = grid.getElementsByTagName("td");
        for(let i=0; i < cells.length; i=i+1){
            cells[i].addEventListener("click", ()=>{
                if(this.state.player==1 && cells[i].style.backgroundColor!="green"){
                    // var row=i%6
                    // var col=Math.floor(i/6)
                    // console.log(row, col)
                    for(let j=i; j<cells.length; j=j+6){     
                        if(cells[j].style.backgroundColor!="red" & cells[j].style.backgroundColor!="green"){
                            
                            const a=j
                            cells[j].style.backgroundColor="red";
                            cells[j-6].style.backgroundColor="white"
                            cells[a].style.backgroundColor="white"
                        }
                    }
                    console.log(i)
                    cells[i].style.backgroundColor="red";
                    // console.log(i)
                    this.CALL(i)
                    this.setState({player:2});
                }
                else if(this.state.player==2 && cells[i].style.backgroundColor!="red"){
                    cells[i].style.backgroundColor="green";
                    // cells[(i-1)].style.backgroundColor="None"
                    // console.log(i)
                    this.CALL(i)
                    var row=Math.floor(i/6)
                    var col=i%6
                    // console.log(row, col)
                    this.setState({player:1});
                }
            });
        }
    }
}
export default Board;
