    }
    render(){ 
        return (
            <div class="page">
                {/* <h2>Welcome to the Game</h2> */}
                <div id="outline">
                    <div class="selection">
                        <div class="in1">
                            <label class="names">player1</label><br/>
                            <input class="input1" type="text" name="player1" placeholder="player1"></input>
                            <span class="red"></span>
                        </div>
                        <div class="in2">
                        <label class="names">player2</label><br/>
                            <input class="input2" type="text" name="player2" placeholder="player2"></input>
                            <span class="green"></span>
                        </div>
                    </div>
                    <table>
                        <tbody>
                        <tr><td id="1"></td><td id="2"></td><td id="3"></td><td id="4"></td><td id="5"></td><td id="6"></td><td id="6"></td></tr>
