                        </tbody>
                    </table>
                </div>
                <h3 id="player"></h3>
                <h2 id="result"></h2>
                <div id="start"></div>
            </div>
        );
    }
    
    checkWinner = (b) =>{
        let w = (this.checkVertical(b) || this.checkDiagonalRight(b) || this.checkDiagonalLeft(b) || this.checkHorizontal(b))
            if(w === 1){
                document.getElementById("player").style.display = "none";
                document.getElementById("result").innerHTML = this.state.play1+" won the Game"
                document.getElementById("start").innerHTML=`<button class="btnre" onclick=${"location.reload()"}>play Again</button>`;
                document.getElementById("outline").style.display = "none";
                // break;
            }
            else if(w === 2){
                document.getElementById("player").style.display = "none";
                document.getElementById("result").innerHTML = `<h2>${this.state.play2}  Won The Game</h2>
                <h4>${this.state.play1}  Better Luck Next Time `;
                document.getElementById("start").innerHTML=`<button class="btnre" onclick=${"location.reload()"}>play Again</button>`;
                document.getElementById("outline").style.display = "none";
                // break;
            }
            // return w
    }
    checkVertical(board) {
        for (let r = 3; r < 6; r++) {
        for (let c = 0; c < 7; c++) {
            if (board[r][c]) {
            if (board[r][c] === board[r - 1][c] &&
                board[r][c] === board[r - 2][c] &&
                board[r][c] === board[r - 3][c]) {
                return board[r][c];    
            }
            }
        }
        }
    }

    checkHorizontal(board) {
        for (let r = 0; r < 6; r++) {
        for (let c = 0; c < 4; c++) {
            if (board[r][c]) {
            if (board[r][c] === board[r][c + 1] && 
                board[r][c] === board[r][c + 2] &&
                board[r][c] === board[r][c + 3]) {
                return board[r][c];
            }
            }
        }
        }
    }
    
    checkDiagonalRight(board) {
        for (let r = 3; r < 6; r++) {
        for (let c = 0; c < 4; c++) {
            if (board[r][c]) {
            if (board[r][c] === board[r - 1][c + 1] &&
                board[r][c] === board[r - 2][c + 2] &&
                board[r][c] === board[r - 3][c + 3]) {
                return board[r][c];
            }
            }
        }
        }
    }
    
    checkDiagonalLeft(board) {
        for (let r = 3; r < 6; r++) {
        for (let c = 3; c < 7; c++) {
            if (board[r][c]) {
            if (board[r][c] === board[r - 1][c - 1] &&
                board[r][c] === board[r - 2][c - 2] &&
                board[r][c] === board[r - 3][c - 3]) {
                return board[r][c];
            }
            }
        }
        }
    }

    componentDidMount()
    {
        const grid= document.getElementById("outline");
        const cells = grid.getElementsByTagName("td");
        for(let i=0; i < cells.length; i++){
            // document.getElementById("player").innerHTML="player 1 Turn"
            cells[i].addEventListener("click", ()=>{
                var col = i%7
                var row = Math.floor(i/7)
                if(cells[i].style.backgroundColor !== "red" && cells[i].style.backgroundColor !=="green"){
                    if(this.state.player === 1){
                        // var p1=document.getElementById("player1")[0].value;
                        var p1=document.getElementsByName("player2")[0].value
                        this.setState({play2:p1});
                        console.log(p1)
                        document.getElementById("player").innerHTML="Next turn: "+`<span style="font-size:30px; background-color:green">${p1 }</span>`;
                        cells[i].style.backgroundColor="red";
                        var b = this.state.board
                        b[row][col] = 1
                        this.setState({board: b})
                        console.log(this.state.board, row, col)
                        this.checkWinner(b)
                        this.setState({player:2});
                    }
                    else if(this.state.player===2){
                        cells[i].style.backgroundColor="green";
                        var p2=document.getElementsByName("player1")[0].value
                        this.setState({play1:p2});
                        console.log(p2)
                        document.getElementById("player").innerHTML="Next turn: "+`<span style="font-size:30px; background-color:red">${p2}</span>`;
                        b = this.state.board
                        b[row][col] = 2
                        this.setState({board: b})
                        console.log(this.state.board, row, col)
                        this.checkWinner(b)
                        this.setState({player:1});
                    }
                }
            });
        }
    }
        
